INSERT INTO VIDEO_DESCRIPTION (ID,DESCRIPTION )
VALUES ('c09cbdbc-e30f-4fcd-ad35-e076b19c8f06','A wild, freeform, Rabelaisian trip through the darkest recesses of Edinburgh low-life, focusing on Mark Renton and his attempt to give up his heroin habit, and how the latter affects his relationship with family and friends: Sean Connery wannabe Sick Boy, dimbulb Spud, psycho Begbie, 14-year-old girlfriend Diane, and clean-cut athlete Tommy, who''s never touched drugs but can''t help being curious about them...                Written by Michael Brooke <michael@everyman.demon.co.uk> tags: Drama Ewan McGregor Ewen Bremner Jonny Lee Miller ');

INSERT INTO VIDEO_ITEM  (ID,TITLE,SUB_TITLE ,IMAGE_SRC ,VIDEO_SRC  )
