INSERT INTO VIDEO_DESCRIPTION (ID,DESCRIPTION )
VALUES ('3056d551-7c87-444d-8c52-8ef8339946bf','A cyborg is sent from the future on a deadly mission. He has to kill Sarah Connor, a young woman whose life will have a great significance in years to come. Sarah has only one protector - Kyle Reese - also sent from the future. The Terminator uses his exceptional intelligence and strength to find Sarah, but is there any way to stop the seemingly indestructible cyborg ?                Written by Colin Tinto <cst@imdb.com> tags: Action Sci-Fi Arnold Schwarzenegger Linda Hamilton Michael Biehn ');

INSERT INTO VIDEO_ITEM  (ID,TITLE,SUB_TITLE ,IMAGE_SRC ,VIDEO_SRC  )
